import React, { Component } from 'react';
import { Route } from 'react-router-dom';

import CheckoutSummary from './../../components/Order/CheckoutSummary/CheckoutSummary';
import ContactData from './ContactData/ContactData';

class Checkout extends Component {
  state = {
    ingredients: null,
    price: 0
  };

  onCheckoutCancelledHandler = () => {
    this.props.history.goBack();
  }
  
  onCheckoutContinuedHandler = () => {
    this.props.history.replace('/checkout/contact-data');
  }

  componentDidMount() {
    const query = new URLSearchParams(this.props.location.search);
    const ingredients = {};
    let price = 0;

    for (let param of query.entries()) {
      if (param[0] == 'price') {
        price = param[1];
      } else {
        ingredients[param[0]] = + param[1];
      }
    }

    this.setState({ ingredients: ingredients, totalPrice: price });
  }

  render() {
    return (
      <div>
        <CheckoutSummary 
          ingredients={this.state.ingredients} 
          checkoutContinued={this.onCheckoutContinuedHandler}
          checkoutCancelled={this.onCheckoutCancelledHandler}
          />
          <Route path={this.props.match.path + '/conatact-data'} render = {() => (<ContactData ingredients={this.state.ingredients} price={this.state.price}/>)}/>
      </div>
    );
  }
}

export default Checkout;
