import * as actionTypes from './actions';

const initialState = {
  ingredients: null,
  totalPrice: 4
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case 'ADD_INGREDIENT':
      return { ...state, ...payload }
    case 'REMOVE_INGREDIENT':
      return { ...state, ...payload }

    default:
      return state
  }
}


export default reducer;
